	(
		basket_item_id 
		,users_user_id
		,products_product_id 
		,basket_volume
		,basket_grind 
		,basket_product_count
	)
	values
	(
		#{basket_item_id}
		,#{users_user_id}
		,#{product_id}
		,#{basket_volume}
		,#{basket_grind}
		,#{basket_product_count}
	)
